Get-NetTCPConnection -AppliedSetting Internet | where -Value "127.0.0.1" -NotIn -Property RemoteAddress | where -Property OwningProcess -In -Value (Get-Process -Name League*).Id | Format-Table @{Label = "RemoteAddress";Expression = {[string]($_.RemoteAddress)}},@{Label = "RemotePort";Expression = {[int]($_.RemotePort)}},@{Label = "OwningProcess"; Expression={[string](Get-Process -Id $_.OwningProcess).Name}}
